import {fetchNewsBegin, fetchNewsFailure, fetchNewsSuccess} from "./news.actions";

const fetchNews = () => {
  return dispatch => {
    dispatch(fetchNewsBegin());
    return fetch('https://finnhub.io/api/v1/company-news?symbol=AAPL&from=2021-03-01&to=2021-03-15&token=bpjsf67rh5r9328ecgvg')
      .then(handleErrors)
      .then(res => res.json())
      .then(json => {
        dispatch(fetchNewsSuccess(json.news));
        return json.news;
      })
      .catch(error => dispatch(fetchNewsFailure(error)));
  };
}

function handleErrors(response) {
  if (!response.ok) {
    throw Error(response.statusText);
  }
  return response;
}

export default fetchNews;
