import { ADD_TO_BOOKMARKS } from "./actions";
import { addItemToBookmark } from "./utility";

const initialState = {
  allBookmarks: [],
  bookmarked: false,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case ADD_TO_BOOKMARKS:
      return {
        ...state,
        allBookmarks: addItemToBookmark(state.allBookmarks, action.payload),
        bookmarked: true,
      };

    default:
      return state;
  }
}
