import {
  FETCH_NEWS_BEGIN,
  FETCH_NEWS_SUCCESS,
  FETCH_NEWS_FAILURE
} from './news.actions';

const initialState = {
  news: null,
  loading: false,
  error: null
};

export default function newsReducer(state = initialState, action) {
  switch(action.type) {
    case FETCH_NEWS_BEGIN:
      console.log('start')

      return {
        ...state,
        loading: true,
        error: null
      };

    case FETCH_NEWS_SUCCESS:
      console.log('success', state)


      return {
        ...state,
        loading: false,
        news: action.payload.news
      };

    case FETCH_NEWS_FAILURE:
      console.log('fail')

      return {
        ...state,
        loading: false,
        error: action.payload.error,
        news: []
      };

    default:
      return state;
  }
}
