export const addToBookmark = (allBookmarkedItems, addToBookmarkItem) => {
  const existsInBookmarks = allBookmarkedItems.find(
    (bookmarkItem) => bookmarkItem.id === addToBookmarkItem.id
  );

  if (existsInBookmarks) {
    return allBookmarkedItems.map((bookmarkItem) =>
      bookmarkItem.id === addToBookmarkItem.id
        ? { ...bookmarkItem, existing: true }
        : bookmarkItem
    );
  }
};
