export const addToBookmark = (allBookmarks, addToBookmarkItem) => {
  const existsInBookmarks = allBookmarks.find(
    (bookmarkItem) => bookmarkItem.id === addToBookmarkItem.id
  );

  if (existsInBookmarks) {
    return allBookmarks.map((bookmarkItem) =>
      bookmarkItem.allBookmarks.id === addToBookmarkItem.allBookmarks.id
        ? { ...bookmarkItem, existing: true }
        : bookmarkItem
    );
  }
  console.log(allBookmarks);
  return [...allBookmarks, { ...addToBookmark, existing: true }];
};
