import {FETCH_NEWS} from "./news.types";

const INITIAL_STATE = {
  allNews: null
}


const newsReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case FETCH_NEWS:
      return {
        ...state,
        allNews: fetch('https://finnhub.io/api/v1/company-news?symbol=AAPL&from=2021-03-01&to=2021-03-15&token=bpjsf67rh5r9328ecgvg')
          .then(response => response.json())
          .payload
      }
      console.log(state)
    default:
      return state;
  }

}
export default newsReducer;
