import {fetchNewsFailure, fetchNewsSuccess} from "./news.actions";

export const getNewsMiddleware = store => dispatch => action => {
  switch (action.type) {
    case "FETCH_NEWS_BEGIN":
      const news =  fetch('https://finnhub.io/api/v1/company-news?symbol=AAPL&from=2021-03-01&to=2021-03-15&token=bpjsf67rh5r9328ecgvg')
        .then(handleErrors)
        .then(res => res.json())
        .then(json => {
          dispatch(fetchNewsSuccess(json.news));
          return json.news;
        })
        .catch(error => dispatch(fetchNewsFailure(error)))
      break;
    default:

  }
  return dispatch(action)
}
