import React, {useEffect} from "react";
import './styles.css'
import {useDispatch, useSelector} from "react-redux";
import {fetchNewsBegin} from "../../redux/News/actions";
import {getNews} from "../../redux/News/selectors";
import LatestItem from "../../Components/LatestItem";
import PostItem from "../../Components/PostItem";
import Pagination from "../../Components/Pagination";

const Home = () => {

  const dispatch = useDispatch();
  const news = useSelector(getNews);

     useEffect(() =>{
       dispatch(fetchNewsBegin())
     },[])

  const postItems = news.splice(1, 7);
  console.log(postItems);

  return (
    <div className='container'>
      <div className="row">
        <div className="col-xl-4 col-lg-6 col-md-6 pb-4">
          {<LatestItem/>}
        </div>
        <div className="col-xl-8 col-lg-6 col-md-6 ">
          <div className="row home">
            <div className=" col-xl-4 col-lg-6 col-md-10 col-sm-6 pb-4">
              {news.slice(1, 7).map((data) => <PostItem postData={data} />)}
            </div>
          </div>
            <Pagination/>
        </div>
      </div>
    </div>
    )
}

export default Home;
