import React from "react";
// import PostItem from "../../Components/PostItem/PostItem";

import {connect} from "react-redux";
import {fetchNews} from "../../redux/News/news.actions";

class Home extends React.Component {

    componentDidMount()
    {
      this.props.dispatch(fetchNews())
    }
  render () {
    const { error, loading, news } = this.props;

    if (error) {
      return <div>Error! {error.message}</div>;
    }

    if (loading) {
      return <div>Loading...</div>;
    }

    return (
    <div className='container'>
      <div className="row">
        <div className="col-xl-4 col-lg-6 col-md-6 pb-4 latest">
          {/*{<LatestItem latestPostData={news}/>}*/}
        </div>
        <div className="col-xl-8 col-lg-6 col-md-6 ">
          <div className="row phones">
            <ul>
            {news.map(news => <li key={news.id}>{news.headline}</li>)}
            </ul>
            {/*{news.slice(1, 7).map((data) => <PostItem postData={data}/>)}*/}
            <div className="row pagination">
              <div className="page-navigation pb-4">
                <div className="pages">1-6 out of 24</div>
                <div className="pages-btn">
                  <button className="previous">Previous</button>
                  <button className="next">Next</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    )}
}
function test() {
  console.log(this.props.news)
}
setTimeout(test, 2000)

const mapStateToProps = state => ({
  news: state.news.items,
  loading: state.news.loading,
  error: state.news.error
});
export default connect(mapStateToProps)(Home);
