import { ADD_TO_BOOKMARKS } from "./actions";

const initialState = {
  allBookmarks: [],
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case ADD_TO_BOOKMARKS:
      const addToBookmark = (allBookmarkedItems, addToBookmarkItem) => {
        const existsInBookmarks = allBookmarkedItems.find(
          (bookmarkItem) => bookmarkItem.id === addToBookmarkItem.id
        );

        if (existsInBookmarks) {
          return allBookmarkedItems.map(
            (bookmarkItem) => bookmarkItem.id === addToBookmarkItem.id
          );
        }
      };
      return {
        ...state,
        allBookmarks: [...state.allBookmarks, action.payload],
      };
    default:
      return state;
  }
}
