export const addItemToBookmark = (allBookmarks, itemToAdd) => {
  const existingItem = allBookmarks.find((item) => item.id === itemToAdd.id);

  if (existingItem) {
    return allBookmarks.map((item) =>
      allBookmarks.id === itemToAdd.id ? { ...item, itemToAdd } : item
    );
  }

  return [...allBookmarks, { ...itemToAdd }];
};

export const removeItemFromBookmarks = (allBookmarks, itemToRemove) => {
  const existingItem = allBookmarks.find((item) => item.id === itemToRemove.id);

  if (existingItem) {
    return allBookmarks.map((item) =>
      allBookmarks.id === itemToRemove.id ? { ...item } : item
    );
  }
  return [...allBookmarks, { ...itemToRemove }];
};
