import { ADD_TO_BOOKMARKS } from "./actions";

const initialState = {
  allBookmarks: [],
  isBookmarked: false,
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case ADD_TO_BOOKMARKS:
      return {
        ...state,
        allBookmarks: [...state.allBookmarks, action.payload],
      };
    default:
      return state;
  }
}
